import React, { useState,useRef, useEffect } from "react";
import whiteEnterText from "../resources/phone_white_text_enter.png";
import { animate, linear } from 'popmotion';
import MainPhone from "./MainPhone";
import bg_point from '../resources/bg_point.png';
import start_enter from '../resources/start_enter.png';
import { udScrollTo } from "./ScrollUtil";

function AppPhone() {
  const mainRef = useRef(null as any);
  const [showContent,setShowContent] = useState(false);

  function onClick(){
    setShowContent(true);
  }

  useEffect(()=>{
    if(showContent){
        mainRef.current.show();
    }

  },[showContent])

  useEffect(() => {
    udScrollTo({ left: 0, top: 0 });
    document.documentElement.style.overflowY = "hidden";
    document.documentElement.style.overflow = "hidden";
    
    return function unmount() {

    };
}, []);

  return (
    <div style={{backgroundColor:"black",overflow:"hidden",width:"100%",height:"100%",WebkitTapHighlightColor:"transparent"}}>
      <Cover onClick={onClick} />
      {<MainPhone ref={mainRef} />}
    </div>
  );
}

const Cover = (props: { onClick: () => void; }) => {

  const divRef = useRef(null as any);
  const bgRef = useRef(null as any);
  const buttonRef = useRef(null as any);
  const onPageClick = () => {
      document.documentElement.style.overflow = "auto";
      document.documentElement.style.overflowY = "auto";
      document.documentElement.style.marginRight = '0';
      divRef.current.style.opacity = 0;
      props.onClick();
      setPlayAnim(false);
  }

  const [fontLoaded,setFontLoaded] = useState(false);
  const [isClick,setClick] = useState(false)
  const [isPlayAnim,setPlayAnim] = useState(true)

  useEffect(()=>{
      if(isClick&&fontLoaded){
          // stopAni()
          // onPageClick()
      }

  },[isClick, fontLoaded])

  useEffect(()=>{
      document.fonts.ready.then(()=>{
          setFontLoaded(true)
      })
  },[])



  const buttonAniStyle = {
      animationDuration: '800ms',
      animationName: 'buttonTransAnimation',
      animationIterationCount: 'infinite',
      animationDirection: 'alternate',
  }
  const keyframesStyle = `
@keyframes buttonTransAnimation {
from {
  transform: translate(-20px,0);
}
to {
  transform: translate(20px,0);
}
}
`;

  const buttonAniPause = (pause) => {
      // if (buttonRef.current) {
      //     let ele = buttonRef.current as HTMLElement;
      //     ele.style.animationPlayState = pause ? 'paused' : 'running';
      // }
  }

  let ani;
  useEffect(() => {
    let ani;
    if(isPlayAnim){
      ani = animate({
        to: 1,
        duration: 1000 * 60 * 2,
        ease: linear,
        repeat: Infinity,
        onUpdate: (progress) => {
            if (bgRef.current) {
                (bgRef.current as HTMLElement).style.transform = `translate(0,-${10000 * progress}px)`
            }
        }
    })
    }
      return () => {
          if(ani!=null){
            ani.stop();
          }
      }
  }, [isPlayAnim]);

  const stopAni = () => {
      if (ani) {
          ani.stop();
          ani = null;
      }
  }

  return (
      <div ref={divRef} onClick={()=>{setClick(true)}} style={{
          transition: 'opacity 400ms linear', flexDirection: 'column',
          opacity: 1,
          backgroundColor:"black",

          width: '100%', height: '100%', display: 'flex', alignItems: 'center', alignContent: 'center', position: 'fixed',
      }}>
          <div ref={bgRef} style={{
              position: 'absolute',
              backgroundImage: `url(${bg_point})`, backgroundRepeat: 'repeat',
              width: '100%', height: '20000px'
          }}></div>
          <img style={{width:343,height:100,position:"absolute",marginTop: "calc((100vh - 100px)*0.35)"}} src={logo_img} />
          <style>{keyframesStyle}</style>
          <img ref={buttonRef} style={{width:70,height:60,position:"absolute",marginTop: "calc((100vh - 150px)*0.85)",...buttonAniStyle, cursor: 'pointer' }} onMouseEnter={() => { buttonAniPause(true) }} onMouseLeave={() => { buttonAniPause(false) }} onClick={onPageClick} src={start_enter} />
          {/* <img style={{width:322,height:66,position:"absolute",marginTop: "calc((100vh - 20px)*0.77)"}} src={text_img}/> */}
          {/* <span style={{lineHeight:"33px",fontWeight:"400",fontSize:18,fontFamily:"Tomorrow",color:'rgba(255, 255, 255, 0.75)',position:"absolute",marginTop: "calc((100vh - 20px)*0.77)"}}>Current Version For PC Browsers Only <br/>Thanks ðŸ«¡ </span> */}
      </div>
  );
}


export default AppPhone;


